mapper.select:
select p.presentid presentNo, u.user_name user_name, pr.productname productname, pr.productprice productprice, p.presentCount presentCount,TO_CHAR(p.presentDate, 'YYYY-MM-DD') presentDate
from present_test p, user_info u, product_test pr
where p.userNo = u.user_id and p.productno = pr.productno and u.user_id = #{userNo}
